import React, { Component } from "react";
import Chart from "react-apexcharts";
import tableData from "../Utils/tableData";

export default class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      series: [
        {
          name: "series1",
          data: [31, 40, 28, 51, 42, 109, 100],
        },
        {
          name: "series2",
          data: [11, 32, 45, 32, 34, 52, 41],
        },
      ],
      options: {
        chart: {
          height: 350,
          width: 900,
          type: "area",
        },
        dataLabels: {
          enabled: false,
        },
        stroke: {
          curve: "smooth",
        },
        xaxis: {
          type: "datetime",
          categories: [
            "2018-09-19T00:00:00.000Z",
            "2018-09-19T01:30:00.000Z",
            "2018-09-19T02:30:00.000Z",
            "2018-09-19T03:30:00.000Z",
            "2018-09-19T04:30:00.000Z",
            "2018-09-19T05:30:00.000Z",
            "2018-09-19T06:30:00.000Z",
          ],
        },
        chart: {
          width: "100%",
          toolbar: {
            show: false,
          },
        },
      },
    };
  }
  render() {
    return (
      <div className="container-routes">
        <div className="dashboard-content">
          <div className="flexy">
            <label>Overview</label>
            <button>
              Add Funds <div className="additive-icons">+</div>
            </button>
          </div>
          <div className="flexy-dashboard">
            <div className="inherit-content">
              <div className="sub-child-content">
                <div className="chart-content">
                  {/* <Chart
                    options={this.state.options}
                    series={this.state.series}
                    type="area"

                  /> */}
                </div>
                <div className="pie-content"></div>
              </div>
              <div className="sub-child-table">
                <label>Referrer</label>
                <div className="table-content">
                  <table className="table table-bordered datatable">
                    <thead>
                      <tr>
                        <th>Location</th>
                        <th>Views</th>
                        <th>Sales</th>
                        <th>Conversation</th>
                        <th>Total</th>
                      </tr>
                    </thead>
                    <tbody>
                      {tableData.map((data, i) => (
                        <tr>
                          <td>{data.location}</td>
                          <td>{data.view}</td>
                          <td>{data.sale}</td>
                          <td>{data.conversation}</td>
                          <td>{data.total}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
                <div className="show-more">Show More</div>
              </div>
            </div>
            <div className="inherit-wide">
              <div className="profile-card">

              </div>
              <div className="product"></div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
