import React, { Component } from "react";
import ProfileImage from "../Assests/profile.jpg";
export default class Navbar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      notifyDropDown: false,
      profileDropDown: false,
    };
  }
  toggleProfileDropDown = (key) => {
    this.setState({
      [key]: !this.state[key],
    });
  };
  render() {
    let { notifyDropDown, profileDropDown } = this.state;
    return (
      <div className="transparent-back">
        <div className="hide-wrap">
          <div className="nav-bar">
            <div>
              <div className="nav-bar__flex">
                <div className="nav-bar__img"></div>
                <div className="nav-bar__flex-inherit">
                  <div className="flexy-search">
                    <div className="search-icon"></div>
                    <input
                      className="input-nav"
                      placeholder="Search transactions, invoices or help"
                    />
                  </div>
                  <div className="profile-flex">
                    <div
                      className="notification-bell"
                      onClick={() =>
                        this.toggleProfileDropDown("notifyDropDown")
                      }
                    >
                      <div className="notify-true"></div>
                    </div>
                    <div
                      className="profile-organized"
                      onClick={() =>
                        this.toggleProfileDropDown("profileDropDown")
                      }
                    >
                      <div className="profile-name">John Doe</div>{" "}
                      <div className="arrow-icon"></div>
                      <img className="profile-image" src={ProfileImage} />{" "}
                    </div>
                  </div>

                </div>
              </div>{" "}
              <div></div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
