import React, { Component } from "react";
import Icons from "../Assests/icons.png";
import "../StyleSheets/style.css";
import SIdebarData from "../Utils/SIdebarData";
export default class Sidebar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      sideBarArray: SIdebarData,
    };
  }
  toggleActiveTab = (key, i) => {
    let { sideBarArray } = this.state;
    let index = i;
    let tempArray = [...sideBarArray];
    if (index === i) {
      tempArray[i] = {
        active: true,
      };
    } else {
      tempArray[i] = {
        active: false,
      };
    }
    this.setState({
      sideBarArray: tempArray,
    });
  };
  render() {
    let { sideBarArray } = this.state;
    console.log(sideBarArray);
    return (
      <div className="side-bar container">
        <div className="logo-container">A C M A</div>
        <ul className="ul-elements">
          {sideBarArray.map((data, i) => (
            <div key={i}>
              {data?.active ? (
                <li onClick={() => this.toggleActiveTab(data.name, i)}>
                  <div className="back-img"></div>
                  <div className="tagName">gdggf</div>
                </li>
              ) : (
                <li onClick={() => this.toggleActiveTab(data.name, i)}>
                  <div className="back-img"></div>
                  <div className="tagName">{data.name}</div>
                </li>
              )}
            </div>
          ))}
        </ul>
      </div>
    );
  }
}
